            function insertAfter(newNode, referenceNode) {
                referenceNode.parentNode.insertBefore(newNode, referenceNode.nextSibling);
            }

            function createThemeLink(name, preload) {
                var integrity = blogThemes[name];
                var base = "https://stackpath.bootstrapcdn.com/bootswatch/3.4.1/";
                var href = name === "default" ? "" : base + name + "/bootstrap.min.css";

                link = document.createElement("link");
                if (preload){
                    link.setAttribute("as", "style"); 
                    link.setAttribute("rel", "preload");
                }
                else {
                    link.setAttribute("id", "theme-link");
                    link.setAttribute("rel", "stylesheet"); 
                }
                link.setAttribute("crossorigin", "anonymous"); 
                link.setAttribute("href", href);
                link.setAttribute("integrity", integrity);
                insertAfter(link, document.querySelector("#bootstrap-link"));
            }

            var preload = true;
            for (var name in blogThemes) {
                createThemeLink(name, preload);
            }

            window.updateThemeLink = function(name) {
                var link = document.querySelector("#theme-link")
                if (link) {
                    document.head.removeChild(link);
                }
                createThemeLink(name);
                window.localStorage.setItem("blog-theme", name);
            }
            var currentTheme = window.localStorage.getItem("blog-theme");
            if (currentTheme) {
                updateThemeLink(currentTheme);
            }

            if (typeof cookieconsent_options !== 'undefined') {
                var currentCookieTheme = window.localStorage.getItem("cookie-theme");
                if (currentCookieTheme) {
                    cookieconsent_options.theme = currentCookieTheme;
                }
            }
        }
    })();

