if (typeof cookieconsent_options !== 'undefined') {
    function loadCookieConsent() {
        $.loadScript('//cdnjs.cloudflare.com/ajax/libs/cookieconsent2/1.0.9/cookieconsent.min.js', 
            { id: "cookie-consent-script" });
    }
    function reloadCookieConsent() {
        window.hasCookieConsent = false;
        $(".cc_banner-wrapper").remove();
        $("#cookie-consent-script").remove();
        $("link").filter(function() {
            return $(this).attr("href").includes("cookieconsent2");
        }).remove();
        document.cookie = 'cookieconsent_dismissed=; expires=Thu, 01 Jan 1970 00:00:01 GMT;';
        loadCookieConsent();
    }
    loadCookieConsent();
}

if (isThemeSelectionEnabled()) {
    initBlogThemes();
    $("#themes-button").removeClass("hidden");
    $("#themes-modal").on("show.bs.modal", function() {
        $("#themes-button").addClass("hidden");
    });
    $("#themes-modal").on("hidden.bs.modal", function() {
        $("#themes-button").removeClass("hidden");
    });
    $("#save-themes-button").click(function() {
        var blogTheme = $("#blog-theme-select").val();
        updateThemeLink(blogTheme);
        var cookieTheme = $("#cookies-theme-select").val();
        updateCookieThemeLink(cookieTheme);
        $("#themes-modal").modal("hide");
    });
    $("#cookie-consent-reset-button").click(function(event) {
        event.preventDefault();
        reloadCookieConsent();
    });
    function updateCookieThemeLink(name) {
        cookieconsent_options.theme = name;
        window.localStorage.setItem("cookie-theme", name);
        reloadCookieConsent();
    }
    function initBlogThemes() {
        var blogThemeSelect = document.querySelector("#blog-theme-select");
        for (var name in blogThemes) {
            var option = document.createElement("option");
            option.value = name;
            option.innerHTML = name[0].toUpperCase() + name.substring(1);
            blogThemeSelect.appendChild(option);
        }
        var currentTheme = window.localStorage.getItem("blog-theme");
        if (!currentTheme) {
            blogThemeSelect.value = "default";
        }
        else {
            blogThemeSelect.value = currentTheme;
        }

        var cookiesThemeSelect = document.querySelector("#cookies-theme-select");
        var currentCookieTheme = window.localStorage.getItem("cookie-theme");
        if (!currentCookieTheme) {
            cookiesThemeSelect.value = "light-floating";
        }
        else {
            cookiesThemeSelect.value = currentCookieTheme;
        }
    }
